## 빈문자 -> 논리형 Falsevar = ''print(var)print(type(var))  # strvar = bool(var)print(var)   # falseprint(type(var))  # bool## 공백문자 -> 논리형 Truevar = ' 'print(var)print(type(var))var = bool(var)print(var)   # trueprint(type(var))  # bool## 문자 -> 논리형 -> 숫자var1 = 'True'var2 = 'False'print(var1)print(var2)print(type(var1))   # strprint(type(var2))   # strvar1 = bool(var1)var2 = bool(var2)print(var1)   # trueprint(var2)   # true -> 문자가 존재하는지 여부를 따지므로 trueprint(type(var1))   # boolprint(type(var2))   # boolprint(var1 + var2)  # 2print(type(var1 + var2))  # int