## 문자 비교char1 = 'A'  # 65char2 = 'S'  # 83print('\'{}\' > \'{}\' : {}'.format(char1, char2, (char1 > char2)))print('\'{}\' >= \'{}\' : {}'.format(char1, char2, (char1 >= char2)))print('\'{}\' < \'{}\' : {}'.format(char1, char2, (char1 < char2)))print('\'{}\' <= \'{}\' : {}'.format(char1, char2, (char1 <= char2)))print('\'{}\' == \'{}\' : {}'.format(char1, char2, (char1 == char2)))print('\'{}\' != \'{}\' : {}'.format(char1, char2, (char1 != char2)))print('---------------------------')# 문자 <-> 아스키코드print('\'A\' -> {}'.format(ord('A')))print('\'S\' -> {}'.format(ord('S')))print('65 -> \'{}\''.format(chr(65)))print('83 -> \'{}\''.format(chr(83)))print('---------------------------')## 문자열 비교str1 = 'Hello'str2 = 'hello'print('{} == {} : {}'.format(str1, str2, (str1 == str2)))print('{} != {} : {}'.format(str1, str2, (str1 != str2)))print('---------------------------')userInputAlphabet = input('알파벳 입력 : ')print('{} : {}'.format(userInputAlphabet, ord(userInputAlphabet)))print('---------------------------')userInputASCII = int(input('아스키 코드 입력 : '))print('{} : {}'.format(userInputASCII, chr(userInputASCII)))