exampleScore = int(input('시험 성적 : '))grade = ''if exampleScore >= 90 :    grade = 'A'elif exampleScore >= 80 :    grade = 'B'elif exampleScore >= 70 :    grade = 'C'elif exampleScore >= 60 :    grade = 'D'else :    grade = 'F'print('성적 : {} \t 학점: {}'.format(exampleScore, grade))print('---------------------------')print('1. 카페라떼(3.5) \t 2.에스프레소(3.0) \t 3.아메리카노(2.0) \t 4.곡물라떼(4.0) \t 5.밀크티(4.3)')selectNumber = int(input('메뉴 선택 : '))print('---------------------------')if selectNumber == 1:    print('메뉴 : 카페라떼')    print('가격 : 3,500원')elif selectNumber == 2:    print('메뉴 : 에스프레소')    print('가격 : 3,000원')elif selectNumber == 3:    print('메뉴 : 아메리카노')    print('가격 : 2,000원')elif selectNumber == 4:    print('메뉴 : 곡물라떼')    print('가격 : 4,000원')else :    print('메뉴 : 밀크티')    print('가격 : 4,300원')print('---------------------------')carDisplacement = int(input('자동차 배기량 : '))if carDisplacement < 1000 :    print('세금 : 100,000원')elif carDisplacement >= 1000 and carDisplacement < 2000 :    print('세금 : 200,000원')elif carDisplacement >= 2000 and carDisplacement < 3000 :    print('세금 : 300,000원')elif carDisplacement >= 3000 and carDisplacement < 4000 :    print('세금 : 400,000원')elif carDisplacement >= 4000 and carDisplacement < 5000 :    print('세금 : 500,000원')elif carDisplacement >= 5000 :    print('세금 : 600,000원')