import randomendNum = 10n = 0while n <= endNum:    print(n)    n += 1print('---------------------------')n = 1while n < 10:    result = 7 * n    print('{} * {} = {}'.format(7, n, result))    n += 1print('---------------------------')endNum = 10n = 0while n < 10:    print('Hello', end = '')    print(' Loop Statement!')    n += 1print('---------------------------')n = 1while n < 101:    if n % 2 == 0:        print('{}은 2의 배수이다.'.format(n))    if n % 3 == 0:        print('{}은 3의 배수이다.'.format(n))    n += 1print('---------------------------')## for, while 차이sum = 0for i in range(1, 11):    sum += iprint('sum : {}'.format(sum))print(' ')sum = 0n = 1while n < 11:    sum += n    n += 1print('sum : {}'.format(sum))print('---------------------------')sum = 0maxInt = 0for i in range(1, 101):    if i % 7 == 0 and sum <= 50:        sum += i        maxInt = i    print('i : {}'.format(i))print('7의 배수의 합이 50이상인 최초의 정수 : {}'.format(maxInt))print('---------------------------')sum = 0maxInt = 0n = 1while n <= 100 and sum <= 50:    n += 1    if n % 7 == 0:        sum += n        maxInt = n    print('n : {}'.format(n))print('7의 배수의 합이 50이상인 최초의 정수 : {}'.format(maxInt))print('---------------------------')currentThickness = 30rotationCount = 0removeThickness = 0.15while currentThickness >= 20:    rotationCount += 1    currentThickness -= removeThicknesssafeRotationCount = rotationCount - 1print('운행 가능 횟수 : {}'.format(safeRotationCount))print('---------------------------')sum = 0date = 0flag = Truewhile flag:    patientCount = random.randint(50, 100)    sum += patientCount    date += 1    print('날짜 : {} \t 오늘 환자수 : {} \t 누적 환자수: {}'.format(date, patientCount, sum))    if sum >= 10000:        flag = Falseprint('---------------------------')