## 반복문 제어(continue)cnt = 0for i in range(1, 100):    if i % 7 != 0:        continue    print('{}는 7의 배수 입니다.'.format(i))    cnt += 1print('99까지의 정수 중 7의 배수는 {}개 입니다.'.format(cnt))print('---------------------------')## 반복문 제어(break)num = 0while True:    print('Hello! ')    num += 1    if(num >= 5):        breakprint('The End..')print('---------------------------')sum = 0searchNum = 0for i in range(100):    sum += i    if sum > 100:        searchNum = i        breakprint('searchNum : {}'.format(searchNum))print('---------------------------')result = 1num = 0for i in range(1, 11):    result *= i    if result > 50:        num = i        breakprint('num : {}, result : {}'.format(num, result))print('---------------------------')limitWeight = 2200currentWeight = 800date = 1while True:    if currentWeight >= limitWeight:        break    currentWeight += 70    date += 1print('이유식 중단일 : {}일'.format(date))print('---------------------------')## 중첩 반복문for i in range(1, 10):    for j in range(i):        print('*', end='')    print()print('---------------------------')for i in range(10, 0, -1):    for j in range(i):        print('*', end='')    print()print('---------------------------')for i in range(1, 10):    for j in range(2, 10):        result = j * i        print('{} * {} = {}\t'.format(j, i, result), end='')    print()