import mathimport time## 기본 내장 모듈listVar = [2, 5, 3.14, 58, 10, 2]print(f'sum(listVar): {sum(listVar)}')print('---------------------------')## 최대값print(f'max(listVar): {max(listVar)}')print('---------------------------')## 최소값print(f'min(listVar): {min(listVar)}')print('---------------------------')## 거듭제곱print(f'pow(13, 2): {pow(13,2)}')print('---------------------------')## 반올림print(f'round(3.141592, 1): {round(3.141592, 1)}')print(f'round(3.141592, 2): {round(3.141592, 2)}')print('---------------------------')print()#### math 사용## 절대값print(f'math.fabs(-10): {math.fabs(-10)}')print(f'math.fabs(-0.12895): {math.fabs(-0.12895)}')print('---------------------------')## 올림print(f'math.ceil(5.21): {math.ceil(5.21)}')print(f'math.ceil(-5.21): {math.ceil(-5.21)}')print('---------------------------')## 내림print(f'math.floor(5.21): {math.floor(5.21)}')print(f'math.floor(-5.21): {math.floor(-5.21)}')print('---------------------------')## 버림print(f'math.trunc(5.21): {math.trunc(5.21)}')print(f'math.trunc(-5.21): {math.trunc(-5.21)}')print('---------------------------')## 최대공약수print(f'math.gcd(14, 21): {math.gcd(14, 21)}')print('---------------------------')## 최소공배수print(f'math.lcm(14, 21): {math.lcm(14, 21)}')print('---------------------------')## 팩토리얼print(f'math.factorial(5): {math.factorial(5)}')print('---------------------------')## 제곱근print(f'math.sqrt(4): {math.sqrt(4)}')print('---------------------------')#### time 사용lt = time.localtime()print(f'time.localtime(): {lt}')print('---------------------------')print(f'lt.tm_year: {lt.tm_year}')print(f'lt.tm_mon: {lt.tm_mon}')print(f'lt.tm_mday: {lt.tm_mday}')print(f'lt.tm_hour: {lt.tm_hour}')print(f'lt.tm_min: {lt.tm_min}')print(f'lt.tm_sec: {lt.tm_sec}')print(f'lt.tm_wday: {lt.tm_wday}')print('---------------------------')