class Car:    def __init__(self, color, length):        self.color = color        self.length = length    def doStop(self):        print('STOP!!')    def doStart(self):        print('START!!')    def printCarInfo(self):        print(f'self.color = {self.color}')        print(f'self.length = {self.length}')car1 = Car('Red', 200)car2 = Car('Blue', 300)car1.printCarInfo()car2.printCarInfo()car1.doStop()car1.doStart()print('---------------------------')class NewGenerationPC:    def __init__(self, name, cpu, memory, ssd):        self.name = name        self.cpu = cpu        self.memory = memory        self.ssd = ssd    def doExcel(self):        print('Excel Run!')    def doPhotoshop(self):        print('Photoshop Run!')    def printPCInfo(self):        print(f'self.name = {self.name}')        print(f'self.cpu = {self.cpu}')        print(f'self.memory = {self.memory}')        print(f'self.ssd = {self.ssd}')myPC = NewGenerationPC('myPC', 'i7', '16G', '512G')myPC.printPCInfo()print()friendPC = NewGenerationPC('friendPC', 'i9', '32G', '1TB')friendPC.printPCInfo()print()## 속성 변경myPC.cpu = 'i9'myPC.memory = '64G'myPC.ssd = '2TB'myPC.printPCInfo()print('---------------------------')class Calculator:    def __init__(self):        self.number1 = 0        self.number2 = 0        self.result = 0    def add(self):        self.result = self.number1 + self.number2        return self.result        def sub(self):            self.result = self.number1 - self.number2        return self.result        def mul(self):            self.result = self.number1 * self.number2        return self.result        def div(self):            self.result = self.number1 / self.number2        return self.resultcal1 = Calculator()cal1.number1 = 10cal1.number2 = 20print(f'{cal1.add()}')