class Robot:    def __init__(self, color, height, weight):        self.color = color        self.height = height        self.weight = weight    def printRobotInfo(self):        print(f'color: {self.color}')        print(f'height: {self.height}')        print(f'weight: {self.weight}')rb1 = Robot('Red', 200, 80)rb1.printRobotInfo()print()rb2 = Robot('Yellow', 300, 120)rb3 = rb1   # 얕은복사print(f'rb1.color: {rb1.color}')rb3.color = 'Gray'print(f'rb3.color: {rb3.color}')print(f'rb1.color: {rb1.color}')print()## 깊은복사import copyrb1 =  Robot('Red', 200, 80)rb2 = copy.copy(rb1)rb1.printRobotInfo()rb2.printRobotInfo()print()rb2.color = 'Blue'rb1.printRobotInfo()rb2.printRobotInfo()print('---------------------------')## 배열의 얕은복사, 깊은복사scores = [1, 2, 3, 4, 5, 6, 7]scoresCopy = []scoresCopy = scoresprint(f'id(scores): {id(scores)}')print(f'id(scoresCopy): {id(scoresCopy)}')print()for s in scores:    scoresCopy.append(s)print(f'id(scores): {id(scores)}')print(f'id(scoresCopy): {id(scoresCopy)}')print()scoresCopy.extend(scores)   # 깊은복사print(f'id(scores): {id(scores)}')print(f'id(scoresCopy): {id(scoresCopy)}')print()scoresCopy = scores.copy()   # 깊은복사print(f'id(scores): {id(scores)}')print(f'id(scoresCopy): {id(scoresCopy)}')print('---------------------------')