class NotUseZeroException(Exception):    def __init__(self, n):        super().__init__(f'{n}은(는) 사용할 수 없습니다!')def divCalculator(num1, num2):    if num2 == 0:        raise NotUseZeroException(num2)    else:        print(f'{num1} / {num2} = {num1 / num2}')num1 = int(input('input number1: '))num2 = int(input('input number2: '))try:    divCalculator(num1, num2)except NotUseZeroException as e:    print(e)print('---------------------------')class PasswordLengthShortException(Exception):    def __init__(self, password):        super().__init__(f'{password}: 길이가 5 미만입니다.')class PasswordLengthLongException(Exception):    def __init__(self, password):        super().__init__(f'{password}: 길이가 10을 초과하였습니다.')class PasswordLengthWrongException(Exception):    def __init__(self, password):        super().__init__(f'{password}: 잘못된 비밀번호 입니다.')adminPW = input('input admin password: ')try:    if len(adminPW) < 5:        raise PasswordLengthShortException(adminPW)    elif len(adminPW) > 10:        raise PasswordLengthLongException(adminPW)    else :        if adminPW != 'admin1234':            raise PasswordLengthWrongException(adminPW)        else:            print('빙고!')except Exception as e:    print(f'{e}')