class Robot:    def __init__(self, c, h, w):        self.color = c        self.height = h        self.weight = w    def fire(self):        print('총알 발사!!')    def printRobotInfo(self):        print(f'self.color = {self.color}')        print(f'self.height = {self.height}')        print(f'self.weight = {self.weight}')class NewRobot(Robot):    def __init__(self, c, h, w):        super().__init__(c, h, w)    def fire(self):        # super().fire()        print('레이저 발사!!')robo1 = Robot(50, 50, 50)robo1.fire()robo1.printRobotInfo()print()robo2 = NewRobot(100, 100, 100)robo2.fire()robo2.printRobotInfo()print('---------------------------')class TriangleArea:    def __init__(self, w, h):        self.width = w        self.height = h    def printTriangleAreaInfo(self):        print(f'self.width : {self.width}')        print(f'self.height : {self.height}')    def getArea(self):        return self.width * self.height / 2class NewTriangleArea(TriangleArea):    def __init__(self, w, h):        super().__init__(w, h)    def getArea(self):        return str(super().getArea()) + '㎠'triangle = NewTriangleArea(7, 5)triangle.printTriangleAreaInfo()triangleArea = triangle.getArea()print(f'triangleArea : {triangleArea}')