from flask import Flaskimport timeapp = Flask(__name__)@app.route('/')def hello_world():  # put application's code here    return 'Hello World!'def delay_decorator(function):    def wrapper_function():        time.sleep(2)        function()    return wrapper_function@delay_decoratordef say_hello():    print("Hello")@delay_decoratordef say_bye():    print("Bye")def say_greeting():    print("How are you?")# @delay_decorator 사용say_hello()# 변수에 담아 사용decorated_function = delay_decorator(say_greeting)decorated_function()# 나만의 decorator 만들기 (함수가 실행된 시간 구하기)def speed_calc_decorator(function):    def wrapper_function():        start_time = time.time()        function()        end_time = time.time()        print(f'{function.__name__} run speed: {end_time - start_time}s')    return wrapper_function@speed_calc_decoratordef fast_function():    for i in range(1000000):        i * i@speed_calc_decoratordef slow_function():    for i in range(10000000):        i * ifast_function()slow_function()if __name__ == '__main__':    app.run()